INSERT INTO `diagnosa` (`id_diagnosa`, `judul`, `definisi`, `gejala`) VALUES
(1, 'Atresia Bilier', 'Atresia Bilier adalah suatu keadaan dimana saluran empedu tidak terbentuk atau tidak berkembang secara normal.\r\nFungsi dari sistem empedu adalah membuang limbah metabolik dari hati dan mengangkut garam empedu yang diperlukan untuk mencerna lemak di dalam usus halus.\r\nPada atresia bilier terjadi penyumbatan aliran empedu dari hati ke kandung empedu. Hal ini bisa menyebabkan kerusakan hati dan sirosis hati, yang jika tidak diobati bisa berakibat fatal\r\n', 'Air kemih bayi berwarna gelap'),
(2, 'Atresia Bilier', 'Atresia Bilier adalah suatu keadaan dimana saluran empedu tidak terbentuk atau tidak berkembang secara normal.\r\nFungsi dari sistem empedu adalah membuang limbah metabolik dari hati dan mengangkut garam empedu yang diperlukan untuk mencerna lemak di dalam usus halus.\r\nPada atresia bilier terjadi penyumbatan aliran empedu dari hati ke kandung empedu. Hal ini bisa menyebabkan kerusakan hati dan sirosis hati, yang jika tidak diobati bisa berakibat fatal\r\n', 'Tinja berwarna pucat'),
(3, 'Atresia Bilier', 'Atresia Bilier adalah suatu keadaan dimana saluran empedu tidak terbentuk atau tidak berkembang secara normal.\r\nFungsi dari sistem empedu adalah membuang limbah metabolik dari hati dan mengangkut garam empedu yang diperlukan untuk mencerna lemak di dalam usus halus.\r\nPada atresia bilier terjadi penyumbatan aliran empedu dari hati ke kandung empedu. Hal ini bisa menyebabkan kerusakan hati dan sirosis hati, yang jika tidak diobati bisa berakibat fatal\r\n', 'Kulit berwarna kuning'),
(4, 'Atresia Bilier', 'Atresia Bilier adalah suatu keadaan dimana saluran empedu tidak terbentuk atau tidak berkembang secara normal.\r\nFungsi dari sistem empedu adalah membuang limbah metabolik dari hati dan mengangkut garam empedu yang diperlukan untuk mencerna lemak di dalam usus halus.\r\nPada atresia bilier terjadi penyumbatan aliran empedu dari hati ke kandung empedu. Hal ini bisa menyebabkan kerusakan hati dan sirosis hati, yang jika tidak diobati bisa berakibat fatal\r\n', 'Berat badan tidak bertambah atau penambahan berat badan berlangsung lambat'),
(5, 'Atresia Bilier', 'Atresia Bilier adalah suatu keadaan dimana saluran empedu tidak terbentuk atau tidak berkembang secara normal.\r\nFungsi dari sistem empedu adalah membuang limbah metabolik dari hati dan mengangkut garam empedu yang diperlukan untuk mencerna lemak di dalam usus halus.\r\nPada atresia bilier terjadi penyumbatan aliran empedu dari hati ke kandung empedu. Hal ini bisa menyebabkan kerusakan hati dan sirosis hati, yang jika tidak diobati bisa berakibat fatal\r\n', 'Hati membesar.'),
(6, 'Hirschprung Desease', 'Penyakit Hirschsprung (Megakolon Kongenital) adalah suatu penyumbatan pada usus besar yang terjadi akibat pergerakan usus yang tidak adekuat karena sebagian dari usus besar tidak memiliki saraf yang mengendalikan kontraksi ototnya.', 'Segera setelah lahir, bayi tidak dapat mengeluarkan mekonium (tinja pertama pada bayi baru lahir)'),
(7, 'Hirschprung Desease', 'Penyakit Hirschsprung (Megakolon Kongenital) adalah suatu penyumbatan pada usus besar yang terjadi akibat pergerakan usus yang tidak adekuat karena sebagian dari usus besar tidak memiliki saraf yang mengendalikan kontraksi ototnya.', 'Tidak dapat buang air besar dalam waktu 24-48 jam setelah lahir'),
(8, 'Hirschprung Desease', 'Penyakit Hirschsprung (Megakolon Kongenital) adalah suatu penyumbatan pada usus besar yang terjadi akibat pergerakan usus yang tidak adekuat karena sebagian dari usus besar tidak memiliki saraf yang mengendalikan kontraksi ototnya.', 'Perut menggembung'),
(9, 'Hirschprung Desease', 'Penyakit Hirschsprung (Megakolon Kongenital) adalah suatu penyumbatan pada usus besar yang terjadi akibat pergerakan usus yang tidak adekuat karena sebagian dari usus besar tidak memiliki saraf yang mengendalikan kontraksi ototnya.', 'Muntah'),
(10, 'Hirschprung Desease', 'Penyakit Hirschsprung (Megakolon Kongenital) adalah suatu penyumbatan pada usus besar yang terjadi akibat pergerakan usus yang tidak adekuat karena sebagian dari usus besar tidak memiliki saraf yang mengendalikan kontraksi ototnya.', 'Diare encer (pada bayi baru lahir)'),
(11, 'Hirschprung Desease', 'Penyakit Hirschsprung (Megakolon Kongenital) adalah suatu penyumbatan pada usus besar yang terjadi akibat pergerakan usus yang tidak adekuat karena sebagian dari usus besar tidak memiliki saraf yang mengendalikan kontraksi ototnya.', 'Berat badan tidak bertambah'),
(12, 'Hirschprung Desease', 'Penyakit Hirschsprung (Megakolon Kongenital) adalah suatu penyumbatan pada usus besar yang terjadi akibat pergerakan usus yang tidak adekuat karena sebagian dari usus besar tidak memiliki saraf yang mengendalikan kontraksi ototnya.', 'Malabsorbsi.'),
