INSERT INTO `diagnosa` (`id_diagnosa`, `judul`, `definisi`, `gejala`) VALUES
(99, 'Prematuritas', 'Prematuritas adalah suatu keadaan yang belum matang, yang ditemukan pada bayi yang lahir pada saat usia kehamilan belum mencapai 37 minggu.\r\nPrematuritas (terutama prematuritas yang ekstrim) merupakan penyebab utama dari kelainan dan kematian pada bayi baru lahir. Beberapa organ dalam bayi mungkin belum berkembang sepenuhnya sehingga bayi memiliki resiko tinggi menderita penyakit tertentu.\r\n', 'Pernafasan yang tidak teratur'),
(100, 'Prematuritas', 'Prematuritas adalah suatu keadaan yang belum matang, yang ditemukan pada bayi yang lahir pada saat usia kehamilan belum mencapai 37 minggu.\r\nPrematuritas (terutama prematuritas yang ekstrim) merupakan penyebab utama dari kelainan dan kematian pada bayi baru lahir. Beberapa organ dalam bayi mungkin belum berkembang sepenuhnya sehingga bayi memiliki resiko tinggi menderita penyakit tertentu.\r\n', 'Kantung zakar kecil dan lipatannya sedikit ( anak laki  laki )'),
(101, 'Prematuritas', 'Prematuritas adalah suatu keadaan yang belum matang, yang ditemukan pada bayi yang lahir pada saat usia kehamilan belum mencapai 37 minggu.\r\nPrematuritas (terutama prematuritas yang ekstrim) merupakan penyebab utama dari kelainan dan kematian pada bayi baru lahir. Beberapa organ dalam bayi mungkin belum berkembang sepenuhnya sehingga bayi memiliki resiko tinggi menderita penyakit tertentu.\r\n', 'Labia mayora belum menutupi labia minora ( pada anak perempuan).'),
(102, 'Tuberkulosis pada bayi', 'Tuberkulosis adalah suatu infeksi oleh Mycobacterium tuberculosis yang menyerang berbagai organ, terutama paru-paru.', 'Demam'),
(103, 'Tuberkulosis pada bayi', 'Tuberkulosis adalah suatu infeksi oleh Mycobacterium tuberculosis yang menyerang berbagai organ, terutama paru-paru.', 'Tampak mengantuk'),
(104, 'Tuberkulosis pada bayi', 'Tuberkulosis adalah suatu infeksi oleh Mycobacterium tuberculosis yang menyerang berbagai organ, terutama paru-paru.', 'Tidak kuat menghisap'),
(105, 'Tuberkulosis pada bayi', 'Tuberkulosis adalah suatu infeksi oleh Mycobacterium tuberculosis yang menyerang berbagai organ, terutama paru-paru.', 'Gangguan pernafasan'),
(106, 'Tuberkulosis pada bayi', 'Tuberkulosis adalah suatu infeksi oleh Mycobacterium tuberculosis yang menyerang berbagai organ, terutama paru-paru.', 'Gagal berkembang (tidak terjadi penambahan berat badan)'),
(107, 'Tuberkulosis pada bayi', 'Tuberkulosis adalah suatu infeksi oleh Mycobacterium tuberculosis yang menyerang berbagai organ, terutama paru-paru.', 'Pembesaran hati dan limpa karena organ ini menyaring bakteri tuberkulosis sehingga menyebabkan aktivasi sel-sel darah putih.'),
(108, 'Omfalokel', 'Omfalokel adalah penonjolan dari usus atau isi perut lainnya melalui akar pusar yang hanya dilapisi oleh peritoneum (selaput perut) dan tidak dilapisi oleh kulit.\r\nOmfalokel terjadi pada 1 dari 5.000 kelahiran.\r\nUsus terlihat dari luar melalui selaput peritoneum yang tipis dan transparan (tembus pandang).\r\n', 'Banyaknya usus dan organ perut lainnya yang menonjol pada omfalokel bervariasi, tergantung kepada besarnya lubang di pusar.'),
(109, 'Omfalokel', 'Omfalokel adalah penonjolan dari usus atau isi perut lainnya melalui akar pusar yang hanya dilapisi oleh peritoneum (selaput perut) dan tidak dilapisi oleh kulit.\r\nOmfalokel terjadi pada 1 dari 5.000 kelahiran.\r\nUsus terlihat dari luar melalui selaput peritoneum yang tipis dan transparan (tembus pandang).\r\n', 'Jika lubangnya kecil, mungkin hanya usus yang menonjol; tetapi jika lubangnya besar, hati juga bisa menonjol melalui lubang tersebut.'),
(110, 'Atrial Septal Defect', 'Defek Septum Atrium (ASD, Atrial Septal Defect) adalah suatu lubang pada dinding (septum) yang memisahkan jantung bagian atas (atrium kiri dan atrium kanan)', 'Gejalanya bisa berupa:\r\n\r\nSering mengalami infeksi saluran pernafasan\r\nDispneu (kesulitan dalam bernafas)\r\nSesak nafas ketika melakukan aktivitas\r\nJantung berdebar-debar (palpitasi).\r\n\r\n'),
(111, 'Atrial Septal Defect', 'Defek Septum Atrium (ASD, Atrial Septal Defect) adalah suatu lubang pada dinding (septum) yang memisahkan jantung bagian atas (atrium kiri dan atrium kanan)', 'Pada kelainan yang sifatnya ringan sampai sedang, mungkin sama sekali tidak ditemukan gejala atau gejalanya baru timbul pada usia pertengahan.'),
(112, 'Atresia Anus', 'Atresia Anus (Anus Imperforatus) adalah suatu keadaan dimana lubang anus tidak terbentuk.\r\nKebanyakan bayi yang menderita atresia anus juga memiliki fistula (hubungan abnormal) antara anus dengan uretra, perineum maupun kandung kemih.\r\n', 'Mekonium (tinja pertama pada bayi baru lahir) tidak keluar dalam waktu 24-48 jam setelah lahir'),
(113, 'Atresia Anus', 'Atresia Anus (Anus Imperforatus) adalah suatu keadaan dimana lubang anus tidak terbentuk.\r\nKebanyakan bayi yang menderita atresia anus juga memiliki fistula (hubungan abnormal) antara anus dengan uretra, perineum maupun kandung kemih.\r\n', 'Tinja keluar dari vagina atau uretra'),
(114, 'Atresia Anus', 'Atresia Anus (Anus Imperforatus) adalah suatu keadaan dimana lubang anus tidak terbentuk.\r\nKebanyakan bayi yang menderita atresia anus juga memiliki fistula (hubungan abnormal) antara anus dengan uretra, perineum maupun kandung kemih.\r\n', 'Perut menggembung'),
(115, 'Atresia Anus', 'Atresia Anus (Anus Imperforatus) adalah suatu keadaan dimana lubang anus tidak terbentuk.\r\nKebanyakan bayi yang menderita atresia anus juga memiliki fistula (hubungan abnormal) antara anus dengan uretra, perineum maupun kandung kemih.\r\n', 'Jika disusui, bayi akan muntah'),
(116, 'Sindrom Gawat Nafas', 'Sindroma Gawat Pernafasan (dulu disebut Penyakit Membran Hialin) adalah suatu keadaan dimana kantung udara (alveoli) pada paru-paru bayi tidak dapat tetap terbuka karena tingginya tegangan permukaan akibat kekurangan surfaktan.\r\nAgar bayi bisa bernafas dengan bebas, setelah lahir, alveoli harus tetap terbuka dan terisi dengan udara. Alveoli bisa terbuka lebar karena adanya suatu bahan yang disebut surfaktan.\r\nSurfaktan dihasilkan oleh sel-sel di dalam alveoli dan berfungsi menurunkan tegangan permukaan. Surfaktan dihasilkan oleh paru-paru yang matang, yaitu pada kehamilan 34-37 minggu.\r\n', 'Takipneu (pernafasan cepat)'),
(117, 'Sindrom Gawat Nafas', 'Sindroma Gawat Pernafasan (dulu disebut Penyakit Membran Hialin) adalah suatu keadaan dimana kantung udara (alveoli) pada paru-paru bayi tidak dapat tetap terbuka karena tingginya tegangan permukaan akibat kekurangan surfaktan.\r\nAgar bayi bisa bernafas dengan bebas, setelah lahir, alveoli harus tetap terbuka dan terisi dengan udara. Alveoli bisa terbuka lebar karena adanya suatu bahan yang disebut surfaktan.\r\nSurfaktan dihasilkan oleh sel-sel di dalam alveoli dan berfungsi menurunkan tegangan permukaan. Surfaktan dihasilkan oleh paru-paru yang matang, yaitu pada kehamilan 34-37 minggu.\r\n', 'Gerakan pernafasan yang tidak biasa (retraksi interkostalis, ketika menghirup udara, otot dinding dada tertarik)'),
(118, 'Sindrom Gawat Nafas', 'Sindroma Gawat Pernafasan (dulu disebut Penyakit Membran Hialin) adalah suatu keadaan dimana kantung udara (alveoli) pada paru-paru bayi tidak dapat tetap terbuka karena tingginya tegangan permukaan akibat kekurangan surfaktan.\r\nAgar bayi bisa bernafas dengan bebas, setelah lahir, alveoli harus tetap terbuka dan terisi dengan udara. Alveoli bisa terbuka lebar karena adanya suatu bahan yang disebut surfaktan.\r\nSurfaktan dihasilkan oleh sel-sel di dalam alveoli dan berfungsi menurunkan tegangan permukaan. Surfaktan dihasilkan oleh paru-paru yang matang, yaitu pada kehamilan 34-37 minggu.\r\n', 'Nafasnya pendek dan ketika menghembuskan nafas terdengar suara ngorok'),
